(8027, 193, 'title', 'Листовая диагностика - Отбор образцов'),
(8028, 193, '_title', 'field_5f8eb91154238'),
(8029, 193, 'description', 'Листовая диагностика - Отбор образцов'),
(8030, 193, '_description', 'field_5f8eb92254239'),
